mapper.update:
update ocomment
set ocomm_ct=#{ocomm_ct}
where ocomm_no=#{ocomm_no}

mapper.select:
select * from ocomment
where o_no=#{o_no}
order by ocomm_re_ref, ocomm_re_lev ASC,ocomm_dt asc

select o_lkno
  from oboard
 where o_no=#{o_no}

mapper.update:
update oboard
set o_rc=o_rc+1
where o_no=#{o_no}

update oboard
set o_lk=o_lk+1
where o_no=#{o_no}

update oboard
set o_sj=#{o_sj}, o_ct=#{o_ct} , o_rc=o_rc-1, o_fl=#{o_fl, jdbcType=VARCHAR}
where o_no=#{o_no}

mapper.select:
select count(*) from oboard








		mem_id like #{find_name}




		o_sj like #{find_name}



		o_ct like #{find_name}

select * from 
(select
rownum rnum, o_no, mem_no, mem_id, o_sj, o_ct, o_fl, o_rc,
o_dt, o_lk, o_lkno
from 
(select * from oboard










		mem_id like #{find_name}



		o_sj like #{find_name}



		o_ct like #{find_name}


order by o_no desc)
)
where rnum >= ((#{page}-1) * #{limit}+1)  and rnum <= (#{page} * #{limit})
