mapper.select:
select count(*) from sboardlist







 		mem_id like #{find_name} 



 		s_sj like #{find_name}



 		s_ct like #{find_name}


		select * 
		from 
		  (select rownum rnum, s_no, mem_id,   s_sj, 
		   s_ct, s_fl,      s_rc,
		   s_dt,  s_lk,   S_lkno , scomm_cnt
		   from  
	  	      (select * from sboardlist 







    	 		mem_id like #{find_name} 



    	 		s_sj like #{find_name}



    	 		s_ct like #{find_name}


	  	       order by s_no desc)
	  	   ) 
	   	where rnum >= ((#{page}-1) * #{limit}+1)  and rnum <= (#{page} * #{limit})

mapper.update:
update sboard
	set scomm_cnt = (select count(*) from scomment where s_no=#{s_no})
where s_no=#{s_no}

update sboard 
   set s_rc=s_rc+1 
 where s_no=#{num}

update sboard 
   set s_sj=#{s_sj},
       s_ct=#{s_ct}, 
       s_fl=#{s_fl, jdbcType=VARCHAR}
 where s_no=#{s_no}

mapper.select:
select s_lkno
  from sboard
 where s_no=#{s_no}
